class ViperPresenter:ViperPresenterProtocolView,ViperPresenterProtocolInteractor,PresenterProtocolBase {
    typealias ViewControllerType = ViperViewProtocolPresenter
    typealias RoutingType = ViperRouting
    
    weak var viewController:ViewControllerType?
    var interactor:ViperInteractorProtocolPresenter!
    var routing:RoutingType!

    required init() {
        // init of presenter
    }
    
    
    func loadData() {
        //LOGIC BUSSINESS
        print("LOGIC BUSSINESS AND PRESENTER CALL INTERACTOR")
        interactor.loadData()
    
    }
    
    func setData() {
        
        print("PRESENTER CALL THE VIEW TO SET DATA")
        viewController?.setData()
    }
    
    
    
    
}
